function getBookInfoByDbsCode(dbsCode) {
	return getBookInfoByProperty('dbsCode', dbsCode);
}

function getBookInfoByUnboundCode(unboundCode) {
	return getBookInfoByProperty('unboundCode', unboundCode);
}
function getBookInfoByUsfmCode(usfmCode) {
	return getBookInfoByProperty('usfm', usfmCode);
}
function getBookInfoByOsisCode(usfmCode) {
	return getBookInfoByProperty('osis', usfmCode);
}

function getBookInfoByProperty(propName, value) {

	//console.log('getBookInfoByUsfmCode', propName, value);

	for (var i=0, il=BOOK_DATA.length; i<il; i++) {
		var bookInfo = BOOK_DATA[i];
		if (bookInfo[propName] == value) {
			// console.log('found', bookInfo[propName], bookInfo.dbsCode);
			return bookInfo;
		}
	}
	return null;
}

function addNames(lang, bookList, namesData) {
	for (var i in bookList) {
		var bookKey = bookList[i],
			names = namesData[i],
			bookInfo = getBookInfoByDbsCode(bookKey);

		if (typeof names === 'string') {
			bookInfo.names[lang] = [names];
		} else {
			bookInfo.names[lang] = names;
		}
	}
}

function getNextChapter(dbsChapterCode, bookCodes) {
	
	if (typeof bookCodes == 'undefined') {
		bookCodes = DEFAULT_BIBLE;		
	}	
	
	var dbsCode = dbsChapterCode.substring(0,2),
		chapter = parseInt(dbsChapterCode.substring(2), 10),
		bookInfo = getBookInfoByDbsCode(dbsCode),
		bookIndex = bookCodes.indexOf(dbsCode);

	if (chapter < bookInfo.chapters.length-1) {
		chapter++
		return dbsCode + chapter.toString();
	} else {
		// next book
		if (bookIndex == bookCodes.length -1) {
			return null;
		} else {
			
			var nextBookCode = bookCodes[bookIndex+1];
			
			// LXX blanks
			if (nextBookCode == '') {
				bookCodes[bookIndex+2];
			}
			
			return nextBookCode + '1';
		}
	}
}

function getPrevChapter(dbsChapterCode, bookCodes) {
	
	if (typeof bookCodes == 'undefined') {
		bookCodes = DEFAULT_BIBLE;		
	}
	
	var dbsCode = dbsChapterCode.substring(0,2),
		chapter = parseInt(dbsChapterCode.substring(2), 10),
		bookInfo = getBookInfoByDbsCode(dbsCode),
		bookIndex = bookCodes.indexOf(dbsCode);

	if (chapter > 1) {
		chapter--;
		return dbsCode + chapter.toString();
	} else {
		// prev book
		if (bookIndex == 0) {
			return null;
		} else {
			var prevBookCode = bookCodes[bookIndex-1];
			
			// for LXX where we currently have empty books
			if (prevBookCode == '') {
				prevBookCode = bookCodes[bookIndex-2];
			}			
			
			var prevBookInfo = getBookInfoByDbsCode(prevBookCode);
			
			return prevBookInfo.dbsCode + prevBookInfo.chapters.length.toString();
		}

	}
}


function getBookName(dbsCode, lang) {
	var bookInfo = getBookInfoByDbsCode(dbsCode),
		name = '';

	if (bookInfo != null && bookInfo.names && bookInfo.names[lang]) {
		name = bookInfo.names[lang];

		if (typeof name == 'string') {
			return name;
		} else {
			return name[0];
		}

	} else {
		return null;
	}
}

